import React, { useState, useEffect } from 'react';
import { Modal, Button, Table } from 'react-bootstrap';

const DataTable = () => {
  const [data, setData] = useState([]);
  const [pageNumber, setPageNumber] = useState(1);
  const [pageSize, setPageSize] = useState(10); // Tamaño de página, valor por defecto
  const [showModal, setShowModal] = useState(false);
  const [selectedPerson, setSelectedPerson] = useState(null);

  useEffect(() => {
    fetchData();
  }, [pageNumber, pageSize]); // Se ejecuta cada vez que pageNumber o pageSize cambian

  const fetchData = () => {
    fetch(`http://localhost:8000/api/personas?pageNumber=${pageNumber}&pageSize=${pageSize}`)
      .then(response => response.json())
      .then(data => {
        setData(data); // Aquí asumimos que la respuesta JSON es un array de objetos
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  };

  const handlePrevClick = () => {
    if (pageNumber > 1) {
      setPageNumber(pageNumber - 1);
    }
  };

  const handleNextClick = () => {
    setPageNumber(pageNumber + 1);
  };

  const handlePageSizeChange = (event) => {
    const size = parseInt(event.target.value);
    setPageSize(size);
    setPageNumber(1); // Resetear a la primera página al cambiar el tamaño de página
  };

  const handleShowDetail = (person) => {
    setSelectedPerson(person);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setSelectedPerson(null);
  };

  const renderDetails = (person) => {
    if (!person) return null;

    return (
      <div>
        <h5>Direcciones:</h5>
        <Table striped bordered>
          <tbody>
            <tr>
              <td>Calle:</td>
              <td>{person.calle}</td>
            </tr>
            <tr>
              <td>Número Exterior:</td>
              <td>{person.numero_exterior}</td>
            </tr>
            <tr>
              <td>Número Interior:</td>
              <td>{person.numero_interior}</td>
            </tr>
            <tr>
              <td>Colonia:</td>
              <td>{person.colonia}</td>
            </tr>
            <tr>
              <td>CP:</td>
              <td>{person.cp}</td>
            </tr>
          </tbody>
        </Table>

        <h5>Teléfonos:</h5>
        <Table striped bordered>
          <tbody>
            <tr>
              <td>Registrados:</td>
              <td>{person.telefono}</td>
            </tr>
          </tbody>
        </Table>
      </div>
    );
  };

  return (
    <div className="container mt-3">
      <h1>Consulta de datos</h1>
      <div className="mb-3">
        <label htmlFor="pageSizeInput" className="form-label">Número de registros por página:</label>
        <input 
          type="number" 
          id="pageSizeInput" 
          className="form-control" 
          value={pageSize} 
          onChange={handlePageSizeChange} 
          min="1" 
        />
      </div>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Apellido Paterno</th>
            <th>Apellido Materno</th>
            <th>Ver detalle</th>
          </tr>
        </thead>
        <tbody>
          {data.map(item => (
            <tr key={item.id}>
              <td>{item.nombre}</td>
              <td>{item.paterno}</td>
              <td>{item.materno}</td>
              <td><Button variant="primary" onClick={() => handleShowDetail(item)}>Ver detalle</Button></td>
            </tr>
          ))}
        </tbody>
      </Table>

      <Button variant="primary" onClick={handlePrevClick} disabled={pageNumber === 1}>
        Anterior
      </Button>
      <Button variant="primary" onClick={handleNextClick} className="ml-2">
        Siguiente
      </Button>

      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Detalles de la Persona</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {renderDetails(selectedPerson)}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>
            Cerrar
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default DataTable;
