import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, useHistory } from 'react-router-dom';
import LoginPage from './pages/LoginPage';
import DataFormPage from './pages/DataFormPage';
import DataTablePage from './pages/DataTablePage';
import axios from 'axios';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Estado de autenticación
  const history = useHistory(); // Hook de useHistory para redireccionar

  // Función para manejar el inicio de sesión
  const handleLogin = async (username, password) => {
    try {
      const response = await axios.post("http://localhost:8000/api/login", {
        username,
        password,
      });

      if (response.data && response.data.token) {
        localStorage.setItem("token", response.data.token);
        setIsLoggedIn(true); // Establecer el estado de inicio de sesión como verdadero
        history.push("/form"); // Redirigir a la página de formulario después del inicio de sesión
      } else {
        console.error("Token no recibido en la respuesta");
      }
    } catch (error) {
      console.error("Error al iniciar sesión:", error);
    }
  };

  // Función para manejar el envío del formulario
  const handleFormSubmit = (formData) => {
    // Lógica de envío del formulario
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<LoginPage onLogin={handleLogin} />} />
        <Route path="/form" element={isLoggedIn ? <DataFormPage onSubmit={handleFormSubmit} /> : <LoginPage />} />
        <Route path="/table" element={isLoggedIn ? <DataTablePage /> : <LoginPage />} />
      </Routes>
    </Router>
  );
};

export default App;
