// Login.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = ({ handleLogin, isLoggedIn }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Simulando la llamada POST al endpoint de login
    // En un entorno real, deberías hacer una llamada HTTP aquí
    if (email === 'admin@interesse.com' && password === 'interesseadmin') {
      handleLogin(); // Marca como autenticado en App.js
      navigate('/form'); // Redirige a /form
    } else {
      console.error('Credenciales incorrectas');
    }
  };

  // Si el usuario ya está autenticado, redirige a /form automáticamente
  if (isLoggedIn) {
    navigate('/form');
  }

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
