import React, { useState, useEffect } from 'react';
import { Modal, Button } from 'react-bootstrap';

const DataTable = () => {
  const [data, setData] = useState([]);
  const [pageNumber, setPageNumber] = useState(1);
  const [pageSize, setPageSize] = useState(10); // Tamaño de página, valor por defecto
  const [showModal, setShowModal] = useState(false);
  const [selectedPerson, setSelectedPerson] = useState(null);

  useEffect(() => {
    fetchData();
  }, [pageNumber, pageSize]); // Se ejecuta cada vez que pageNumber o pageSize cambian

  const fetchData = () => {
    fetch(`http://localhost:8000/api/personas?pageNumber=${pageNumber}&pageSize=${pageSize}`)
      .then(response => response.json())
      .then(data => {
        setData(data); // Aquí asumimos que la respuesta JSON es un array de objetos
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  };

  const handlePrevClick = () => {
    if (pageNumber > 1) {
      setPageNumber(pageNumber - 1);
    }
  };

  const handleNextClick = () => {
    setPageNumber(pageNumber + 1);
  };

  const handlePageSizeChange = (event) => {
    const size = parseInt(event.target.value);
    setPageSize(size);
    setPageNumber(1); // Resetear a la primera página al cambiar el tamaño de página
  };

  const handleShowDetail = (person) => {
    setSelectedPerson(person);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setSelectedPerson(null);
  };

  const renderDetails = (person) => {
    if (!person) return null;

    const calles = person.calle.split(', ');
    const numeros_exterior = person.numero_exterior.split(', ');
    const numeros_interior = person.numero_interior.split(', ');
    const colonias = person.colonia.split(', ');
    const cps = person.cp.split(', ');
    const telefonos = person.telefono.split(', ');

    return (
      <div>
        <h5>Direcciones:</h5>
        <table className="table table-striped table-bordered">
          <thead className="table-success">
            <tr>
              <th>Dirección</th>
              <th>Calle</th>
              <th>Número Exterior</th>
              <th>Número Interior</th>
              <th>Colonia</th>
              <th>CP</th>
            </tr>
          </thead>
          <tbody>
            {calles.map((calle, index) => (
              <tr key={index}>
                <td>Dirección {index + 1}</td>
                <td>{calle}</td>
                <td>{numeros_exterior[index]}</td>
                <td>{numeros_interior[index]}</td>
                <td>{colonias[index]}</td>
                <td>{cps[index]}</td>
              </tr>
            ))}
          </tbody>
        </table>
        <h5>Teléfonos:</h5>
        <table className="table table-striped table-bordered">
          <thead className="table-success">
            <tr>
              <th>Teléfono</th>
              <th>Número</th>
            </tr>
          </thead>
          <tbody>
            {telefonos.map((telefono, index) => (
              <tr key={index}>
                <td>Teléfono {index + 1}</td>
                <td>{telefono}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };

  return (
    <div className="container mt-3">
      <h1>Consulta de datos</h1>
      <div className="mb-3">
        <label htmlFor="pageSizeInput" className="form-label">Número de registros por página:</label>
        <input 
          type="number" 
          id="pageSizeInput" 
          className="form-control" 
          value={pageSize} 
          onChange={handlePageSizeChange} 
          min="1" 
        />
      </div>
      <table className="table table-striped table-bordered">
        <thead className="table-success">
          <tr>
            <th scope="col">Nombre</th>
            <th scope="col">Apellido Paterno</th>
            <th scope="col">Apellido Materno</th>
            <th scope="col">Ver detalle</th>
          </tr>
        </thead>
        <tbody>
          {data.map(item => (
            <tr key={item.id}>
              <td>{item.nombre}</td>
              <td>{item.paterno}</td>
              <td>{item.materno}</td>
              <td><button className='btn btn-primary' onClick={() => handleShowDetail(item)}>Ver detalle</button></td>
            </tr>
          ))}
        </tbody>
      </table>
      <div>
        <button className="btn btn-primary" onClick={handlePrevClick} disabled={pageNumber === 1}>
          Anterior
        </button>
        <button className="btn btn-primary ml-2" onClick={handleNextClick}>
          Siguiente
        </button>
      </div>

      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Detalles de la Persona</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {renderDetails(selectedPerson)}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>
            Cerrar
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default DataTable;
