import React, { useState } from "react";
import axios from "axios";

const Login = ({ onLogin }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(null); // Estado para manejar el mensaje de error

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("https://pruebatecnica.jairjuarezdeveloper.com//api/login", {
        email,
        password,
      });

      if (response.data && response.data.token) {
        localStorage.setItem("token", response.data.token);
        console.log("Inicio de sesión exitoso");
        // Redirigir al usuario a la página deseada usando window.location.href
        window.location.href = "/form"; // Redireccionar a la ruta "/form"
      } else {
        console.error("Token no recibido en la respuesta");
        setError("Credenciales incorrectas. Por favor, verifica tus datos."); // Establecer mensaje de error
      }
    } catch (error) {
      if (error.response && error.response.status === 401) {
        console.error("Credenciales incorrectas");
        setError("Credenciales incorrectas. Por favor, verifica tus datos."); // Establecer mensaje de error
      } else {
        console.error("Error al iniciar sesión:", error);
        setError("Error al iniciar sesión. Por favor, inténtalo de nuevo más tarde."); // Establecer mensaje de error
      }
    }
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-2"></div>
        <div className="col-8">
          <h1 className="mb-3">Iniciar sesión</h1>
          {/* Mostrar alerta de error si existe */}
          {error && (
            <div className="alert alert-danger" role="alert">
              {error}
            </div>
          )}
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="exampleInputEmail1" className="form-label">
                Correo electrónico
              </label>
              <input
                type="email"
                className="form-control"
                id="exampleInputEmail1"
                aria-describedby="emailHelp"
                placeholder="example@example.com"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="exampleInputPassword1" className="form-label">
                Contraseña
              </label>
              <input
                type="password"
                className="form-control"
                id="exampleInputPassword1"
                placeholder="*********"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <button type="submit" className="btn btn-primary">
              Iniciar sesión
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
