import React, { useState, useRef } from "react";
import axios from "axios";

const DataForm = () => {
  const [file, setFile] = useState(null);
  const [dragActive, setDragActive] = useState(false);
  const fileInputRef = useRef(null);
  const [alertMessage, setAlertMessage] = useState("");

  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      handleFileUpload(e.dataTransfer.files[0]);
    }
  };

  const handleChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      handleFileUpload(e.target.files[0]);
    }
  };

  const handleFileUpload = (uploadedFile) => {
    const allowedExtensions = ["xlsx", "xls"];
    const fileExtension = uploadedFile.name.split(".").pop().toLowerCase();

    if (allowedExtensions.includes(fileExtension)) {
      setFile(uploadedFile);
      setAlertMessage("");
    } else {
      setFile(null);
      setAlertMessage("Solo se permiten archivos con extensi칩n .xlsx o .xls");
    }

    // Optional: Reset input value to allow re-uploading of the same file
    fileInputRef.current.value = "";
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (file) {
      const formData = new FormData();
      formData.append("file", file);

      try {
        const response = await axios.post(
          "http://localhost:8000/api/upload",
          formData,
          {
            headers: {
              "Content-Type": "multipart/form-data",
            },
          }
        );
        console.log("File uploaded successfully", response.data);
      } catch (error) {
        console.error("Error uploading file", error);
      }
    } else {
      setAlertMessage("Por favor selecciona un archivo v치lido.");
    }
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-2"></div>
        <div className="col-8">
          <h1>Carga tu archivo xlsx</h1>
          <form
            onSubmit={handleSubmit}
            onDragEnter={handleDrag}
            onDragOver={handleDrag}
            onDragLeave={handleDrag}
            onDrop={handleDrop}
            className={`form-control p-5 text-center ${
              dragActive ? "border border-primary" : ""
            }`}
            style={{
              border: dragActive ? "2px dashed #007bff" : "2px dashed #ccc",
            }}
          >
            <div className="mb-3">
              <label htmlFor="formFile" className="form-label">
                Selecciona o arrastra tu archivo
              </label>
              <input
                className="form-control"
                type="file"
                id="formFile"
                ref={fileInputRef}
                onChange={handleChange}
                accept=".xlsx, .xls"
              />
            </div>
            {alertMessage && (
              <div className="alert alert-danger" role="alert">
                {alertMessage}
              </div>
            )}
            <button type="submit" className="btn btn-primary">
              Cargar archivo
            </button>
            {file && <p>Archivo seleccionado: {file.name}</p>}
          </form>
        </div>
      </div>
    </div>
  );
};

export default DataForm;
