import React, { useState, useEffect } from 'react';

const DataTable = () => {
  const [data, setData] = useState([]);
  const [pageNumber, setPageNumber] = useState(1);
  const pageSize = 10; // Tamaño de página, ajustar según tu necesidad

  useEffect(() => {
    fetchData();
  }, [pageNumber]); // Se ejecuta cada vez que pageNumber cambia

  const fetchData = () => {
    fetch(`http://localhost:8000/api/personas?pageNumber=${pageNumber}&pageSize=${pageSize}`)
      .then(response => response.json())
      .then(data => {
        setData(data); // Aquí asumimos que la respuesta JSON es un array de objetos
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  };

  const handlePrevClick = () => {
    if (pageNumber > 1) {
      setPageNumber(pageNumber - 1);
    }
  };

  const handleNextClick = () => {
    setPageNumber(pageNumber + 1);
  };

  return (
    <div className="container mt-3">
      <h1>Consulta de datos</h1>
      <table className="table table-striped table-bordered">
        <thead className="table-success">
          <tr>
            <th scope="col">Nombre</th>
            <th scope="col">Apellido Paterno</th>
            <th scope="col">Apellido Materno</th>
            <th scope="col">Ver detalle</th>
          </tr>
        </thead>
        <tbody>
          {data.map(item => (
            <tr key={item.id}>
              <td>{item.nombre}</td>
              <td>{item.paterno}</td>
              <td>{item.materno}</td>
              <td><button className='btn btn-primary'>Ver detalle</button></td>
            </tr>
          ))}
        </tbody>
      </table>
      <div>
        <button className="btn btn-primary" onClick={handlePrevClick} disabled={pageNumber === 1}>
          Anterior
        </button>
        <button className="btn btn-primary ml-2" onClick={handleNextClick}>
          Siguiente
        </button>
      </div>
    </div>
  );
};

export default DataTable;
