import React, { useState, useRef } from "react";
import axios from "axios"; // Asegúrate de tener axios instalado

const DataForm = ({ onSubmit }) => {
  const [file, setFile] = useState(null);
  const [dragActive, setDragActive] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const fileInputRef = useRef(null);

  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      setFile(e.dataTransfer.files[0]);
      fileInputRef.current.files = e.dataTransfer.files;
    }
  };

  const handleChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      setFile(e.target.files[0]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (file) {
      if (file.type !== 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet') {
        setErrorMessage('Por favor, sube un archivo xlsx válido.');
        setSuccessMessage('');
        return;
      }
      try {
        const formData = new FormData();
        formData.append('file', file);

        const response = await axios.post('http://your-laravel-backend.com/upload', formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });

        if (response.status === 200) {
          setSuccessMessage('Archivo cargado exitosamente.');
          setErrorMessage('');
        } else {
          setErrorMessage('Error al cargar el archivo.');
          setSuccessMessage('');
        }
      } catch (error) {
        setErrorMessage('Error al cargar el archivo.');
        setSuccessMessage('');
      }
    } else {
      setErrorMessage('Por favor, selecciona un archivo.');
      setSuccessMessage('');
    }
  };

  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-2"></div>
        <div className="col-8">
          <h1>Carga tu archivo xlsx</h1>
          {errorMessage && (
            <div className="alert alert-danger" role="alert">
              {errorMessage}
            </div>
          )}
          {successMessage && (
            <div className="alert alert-success" role="alert">
              {successMessage}
            </div>
          )}
          <form
            onSubmit={handleSubmit}
            onDragEnter={handleDrag}
            onDragOver={handleDrag}
            onDragLeave={handleDrag}
            onDrop={handleDrop}
            className={`form-control p-5 text-center ${
              dragActive ? "border border-primary" : ""
            }`}
            style={{
              border: dragActive ? "2px dashed #007bff" : "2px dashed #ccc",
            }}
          >
            <div className="mb-3">
              <label htmlFor="formFile" className="form-label">
                Selecciona o arrastra tu archivo
              </label>
              <input
                className="form-control"
                type="file"
                id="formFile"
                ref={fileInputRef}
                onChange={handleChange}
              />
            </div>
            <button type="submit" className="btn btn-primary">
              Cargar archivo
            </button>
            {file && <p>Archivo seleccionado: {file.name}</p>}
          </form>
        </div>
      </div>
    </div>
  );
};

export default DataForm;
