import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import axios from "axios";
import Login from "./Login";
import DataFormPage from "./DataFormPage";
import DataTablePage from "./DataTablePage";
import { isAuthenticated } from "./auth"; // Importar la función de autenticación

const PrivateRoute = ({ element, path }) => {
  return isAuthenticated() ? element : <Navigate to="/" replace />;
};

const App = () => {
  const handleLogin = async (email, password) => {
    try {
      const response = await axios.post("http://localhost:8000/api/login", {
        email,
        password,
      });

      if (response.data && response.data.token) {
        localStorage.setItem("token", response.data.token);
        console.log("Inicio de sesión exitoso");
        return true;
      } else {
        console.error("Token no recibido en la respuesta");
        return false;
      }
    } catch (error) {
      console.error("Error al iniciar sesión:", error);
      return false;
    }
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Login onLogin={handleLogin} />} />
        <Route path="/form" element={<PrivateRoute element={<DataFormPage />} />} />
        <Route path="/table" element={<PrivateRoute element={<DataTablePage />} />} />
      </Routes>
    </Router>
  );
};

export default App;
