// App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import DataFormPage from './pages/DataFormPage';
import DataTablePage from './pages/DataTablePage';
import LoginPage from './pages/LoginPage';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Función para manejar el login exitoso
  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  // Función para manejar el logout
  const handleLogout = () => {
    setIsLoggedIn(false);
  };

  return (
    <Router>
      <Routes>
        {/* Página de login */}
        <Route
          path="/"
          element={<LoginPage handleLogin={handleLogin} isLoggedIn={isLoggedIn} />}
        />
        {/* Ruta protegida para /form */}
        <PrivateRoute path="/form" element={<DataFormPage />} isLoggedIn={isLoggedIn} />
        {/* Ruta protegida para /table */}
        <PrivateRoute path="/table" element={<DataTablePage />} isLoggedIn={isLoggedIn} />
      </Routes>
    </Router>
  );
};

// Componente para manejar rutas protegidas
const PrivateRoute = ({ element, isLoggedIn, ...rest }) => {
  return isLoggedIn ? (
    <Route {...rest} element={element} />
  ) : (
    <Navigate to="/" replace /> // Redirige al login si no está autenticado
  );
};

export default App;
