import React, { useState, useEffect } from 'react';

const DataTable = () => {
  const [data, setData] = useState([]);
  const [pageNumber, setPageNumber] = useState(1);
  const [pageSize, setPageSize] = useState(10); // Tamaño de página, valor por defecto
  const [totalPages, setTotalPages] = useState(0); // Total de páginas disponibles
  const [totalItems, setTotalItems] = useState(0); // Total de elementos en la consulta

  useEffect(() => {
    fetchData();
  }, [pageNumber, pageSize]); // Se ejecuta cada vez que pageNumber o pageSize cambian

  const fetchData = () => {
    fetch(`http://localhost:8000/api/personas?pageNumber=${pageNumber}&pageSize=${pageSize}`)
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        setData(data.data); // Aquí asumimos que la respuesta JSON tiene una estructura de paginación estándar
        setTotalPages(data.last_page); // Total de páginas
        setTotalItems(data.total); // Total de elementos
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  };

  const handlePrevClick = () => {
    if (pageNumber > 1) {
      setPageNumber(pageNumber - 1);
    }
  };

  const handleNextClick = () => {
    if (pageNumber < totalPages) {
      setPageNumber(pageNumber + 1);
    }
  };

  const handlePageSizeChange = (event) => {
    const size = parseInt(event.target.value);
    setPageSize(size);
    setPageNumber(1); // Resetear a la primera página al cambiar el tamaño de página
  };

  return (
    <div className="container mt-3">
      <h1>Consulta de datos</h1>
      <div className="mb-3">
        <label htmlFor="pageSizeInput" className="form-label">Número de registros por página:</label>
        <input 
          type="number" 
          id="pageSizeInput" 
          className="form-control" 
          value={pageSize} 
          onChange={handlePageSizeChange} 
          min="1" 
        />
      </div>
      <table className="table table-striped table-bordered">
        <thead className="table-success">
          <tr>
            <th scope="col">Nombre</th>
            <th scope="col">Apellido Paterno</th>
            <th scope="col">Apellido Materno</th>
            <th scope="col">Ver detalle</th>
          </tr>
        </thead>
        <tbody>
          {data.map(item => (
            <tr key={item.id}>
              <td>{item.nombre}</td>
              <td>{item.paterno}</td>
              <td>{item.materno}</td>
              <td><button className='btn btn-primary'>Ver detalle</button></td>
            </tr>
          ))}
        </tbody>
      </table>
      <div>
        <button className="btn btn-primary" onClick={handlePrevClick} disabled={pageNumber === 1}>
          Anterior
        </button>
        <button className="btn btn-primary ml-2" onClick={handleNextClick} disabled={pageNumber === totalPages}>
          Siguiente
        </button>
      </div>
      <p className="mt-3">Página {pageNumber} de {totalPages}, Total de registros: {totalItems}</p>
    </div>
  );
};

export default DataTable;
