import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import LoginPage from "./pages/LoginPage";
import DataFormPage from "./pages/DataFormPage";
import DataTablePage from "./pages/DataTablePage";

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = async (username, password) => {
    try {
      const response = await axios.post("http://localhost:8000/api/login", {
        username,
        password,
      });

      if (response.data && response.data.token) {
        localStorage.setItem("token", response.data.token);
        setIsLoggedIn(true);
        console.log("Inicio de sesión exitoso");
      } else {
        console.error("Token no recibido en la respuesta");
      }
    } catch (error) {
      console.error("Error al iniciar sesión:", error);
    }
  };

  const handleFormSubmit = (formData) => {
    // Lógica de envío del formulario
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<LoginPage onLogin={handleLogin} />} />
        <Route path="/form" element={isLoggedIn ? <DataFormPage onSubmit={handleFormSubmit} /> : <Navigate to="/" replace />} />
        <Route path="/table" element={isLoggedIn ? <DataTablePage /> : <Navigate to="/" replace />} />
      </Routes>
    </Router>
  );
};

export default App;
