import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import LoginPage from "./pages/LoginPage";
import DataFormPage from "./pages/DataFormPage";
import DataTablePage from "./pages/DataTablePage";
import axios from "axios";

// Componente de ruta privada
const PrivateRoute = ({ element: Element, isLoggedIn, ...rest }) => {
  return isLoggedIn ? <Element {...rest} /> : <Navigate to="/" replace />;
};

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Estado de autenticación

  // Función para manejar el inicio de sesión
  const handleLogin = async (username, password) => {
    try {
      const response = await axios.post("http://localhost:8000/api/login", {
        username,
        password,
      });

      if (response.data && response.data.token) {
        localStorage.setItem("token", response.data.token);
        setIsLoggedIn(true); // Establecer el estado de inicio de sesión como verdadero
        console.log("Inicio de sesión exitoso"); // Confirmar inicio de sesión
      } else {
        console.error("Token no recibido en la respuesta");
      }
    } catch (error) {
      console.error("Error al iniciar sesión:", error);
    }
  };

  // Función para manejar el cierre de sesión
  const handleLogout = () => {
    localStorage.removeItem("token");
    setIsLoggedIn(false);
    console.log("Sesión cerrada"); // Confirmar cierre de sesión
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<LoginPage onLogin={handleLogin} />} />
        <PrivateRoute
          path="/form"
          element={<DataFormPage onSubmit={() => {}} />}
          isLoggedIn={isLoggedIn}
        />
        <PrivateRoute path="/table" element={<DataTablePage />} isLoggedIn={isLoggedIn} />
      </Routes>
    </Router>
  );
};

export default App;
